import React, { useEffect } from "react";
import { useState } from "react";
import { Dropdown } from "semantic-ui-react";

const TimeDropdown = (props) => {
  const [time, setTime] = useState(null);
  const [timeSlots, setTimeSlots] = useState(null);

  useEffect(() => {
    fetch(`/get_diary/${props.doctor_id}/${props.selectedDate}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        const sortedappts = data.sort(
          (a, b) => b.time_slot_id - a.time_slot_id
        );

        console.log(sortedappts);

        // Need to check if available
        const timeSlots = sortedappts.map((diary) => {
          if (diary.available === "Y") {
            return diary.time_slot;
          }
        });
        setTimeSlots(timeSlots);
      });
  }, []);

  const options = [];

  if (timeSlots !== null) {
    timeSlots.forEach((timeslot, index) => {
      return (options[index] = {
        key: index,
        text: timeslot,
        value: timeslot,
      });
    });
  }

  const handleChange = (_e, { value }) => {
    setTime({ value });
    props.onChosenTime({ value });
  };

  return (
    <div className="time-select">
      <Dropdown
        placeholder="Select choice"
        options={options}
        onChange={handleChange}
        selection
        scrolling
        clearable
      />
    </div>
  );
};

export default TimeDropdown;
