import React, { useState, useEffect } from "react";
import "./BookingContainer.css";
import TimeDropdown from "../TimeDropdown/TimeDropdown";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const DropdownContainer = (props) => {
  const { doctor_id, user, doctor_name } = props;
  const [selectedDate, setSelectedDate] = useState(null);
  const [time, setTime] = useState(null);
  const [diaryId, setDiaryId] = useState(null);

  // Setting Date
  var today_date = new Date();
  var dateInAWeek = new Date();
  dateInAWeek.setDate(dateInAWeek.getDate() + 14);

  const handleTimeUpdate = (value) => {
    setTime(value);
  };

  function formatDate(date) {
    var d = new Date(date),
      month = "" + (d.getMonth() + 1),
      day = "" + d.getDate(),
      year = d.getFullYear();

    if (month.length < 2) month = "0" + month;
    if (day.length < 2) day = "0" + day;

    return [year, month, day].join("-");
  }

  const getDiaryId = () => {
    var formattedDate = formatDate(selectedDate);

    fetch(`/get_diary/${doctor_id}/${formattedDate}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });

    console.log(time);
  };

  return (
    <div className="time-select">
      <h3>Your appointment with Dr. {doctor_name}</h3>
      <div className="date-selection">
        <h5>Please select the date of your appointment</h5>
        <DatePicker
          selected={selectedDate}
          onChange={(date) => setSelectedDate(date)}
          dateFormat="dd/MM/yyyy"
          minDate={today_date}
          maxDate={dateInAWeek}
          isClearable
        ></DatePicker>
        <h5>Please select a time</h5>
        <TimeDropdown onChosenTime={handleTimeUpdate}></TimeDropdown>

        <button className="submit-button" onClick={getDiaryId}>
          Submit!
        </button>
      </div>
    </div>
  );
};

export default DropdownContainer;
