import React, { useState, useEffect } from "react";
import "./PatientAppointments.css";

const PatientAppointments = (props) => {
  const [appointmentList, setAppointmentList] = useState(null);
  const [ApptLoaded, setApptLoaded] = useState(false);

  const doctor_id = props.doctor_id;

  useEffect(() => {
    async function getAppointments() {
      let appointmentQuery = await fetch(
        `/doctor/get_appointments/${doctor_id}/`
      );
      let appointments = await appointmentQuery.json();
      setAppointmentList(appointments);
      setApptLoaded(true);
      console.log(appointments);
    }

    getAppointments();
  }, []);

  //   useEffect(() => {
  //     async function getDoctors() {
  //       try {
  //         let docResponse = await fetch("/get_doctors");
  //         let doctors = await docResponse.json();
  //         setDoctorList(doctors);
  //         setDocLoaded(true);
  //       } catch (err) {
  //         console.log(err);
  //       }
  //     }
  //     getDoctors();
  //   }, []);

  const getPatientName = (appointment) => {
    return (
      <h3>
        {appointment.first_name} {appointment.middle_name}{" "}
        {appointment.last_name}
      </h3>
    );
  };

  const getDate = (appointment) => {
    const dateArray = appointment.diary_date.split(" ");
    const date = dateArray.slice(1, 4);
    var day = dateArray[0];
    return <div>{date.join("/")}</div>;
  };

  const getZoomUrl = (appointment) => {
    const zoomUrl = appointment.videoconference_url;
    console.log("http://" + zoomUrl);
    return (
      <a href={"http://" + zoomUrl} target="_blank">
        Click here to begin your appointment
      </a>
    );
  };

  const deleteAppointment = (appointment_id) => {
    fetch("/delete_booking", {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },

      //make sure to serialize your JSON body
      body: JSON.stringify({
        patient_id: 1,
        diary_id: appointment_id,
      }),
    }).then((response) => {
      window.location.reload();
    });
  };

  console.log(appointmentList);

  if (ApptLoaded) {
    return (
      <div className="appointment-list">
        <div className="patient-appointment-intro">
          <h2>Please read carefully</h2>
          <p>
            If you wish to upload any medical information or images such as
            X-rays or CAT scans, please do so by clicking the 'Upload Files'
            button and attaching the files you wish to provide the Practitioner
          </p>
          <p>
            When it is time for your appointment, please click on the link
            underneath 'Zoom URL' to begin your video appointment.
          </p>
        </div>
        {appointmentList.map((appointment) => (
          <div className="appointment-container">
            <div className="calendar-container">
              <img className="calendar-pic" src="calendar.png"></img>
              <div className="time-details">
                <h2>{getDate(appointment)}</h2>
                <h3>
                  {appointment.time_from} - {appointment.time_to}
                </h3>
              </div>
            </div>
            <div className="appointment-box">
              <h2>Patient</h2>
              {getPatientName(appointment)}
            </div>
            <div className="appointment-box">
              <h2>Zoom URL</h2>
              {getZoomUrl(appointment)}
            </div>
            <div className="appointment-box">
              <h2>Cancel appointment</h2>
              <button
                onClick={() => {
                  deleteAppointment(appointment.diary_id);
                  //   forceUpdate();
                }}
              >
                Cancel the appointment
              </button>
            </div>
          </div>
        ))}
      </div>
    );
  } else {
    return <></>;
  }
};

export default PatientAppointments;
