import React, { useState, useEffect } from "react";
import "./AppointmentContainer.css";
import DoctorList from "../DoctorList/DoctorList";

const AppointmentContainer = (props) => {
  const [appointmentList, setAppointmentList] = useState(null);
  const [doctorList, setDoctorList] = useState(null);
  const [ApptLoaded, setApptLoaded] = useState(false);

  const [docLoaded, setDocLoaded] = useState(false);

  const patient_id = props.patient_id;

  useEffect(() => {
    async function getAppointments() {
      let appointmentQuery = await fetch(`/get_appointments/${patient_id}/`);
      let appointments = await appointmentQuery.json();
      setAppointmentList(appointments);
      setApptLoaded(true);
    }

    getAppointments();
  }, []);

  useEffect(() => {
    async function getDoctors() {
      try {
        let docResponse = await fetch("/get_doctors");
        let doctors = await docResponse.json();
        setDoctorList(doctors);
        setDocLoaded(true);
      } catch (err) {
        console.log(err);
      }
    }
    getDoctors();
  }, []);

  const getDoctorName = (doctor_id) => {
    console.log(doctorList.first_name);
  };

  if (ApptLoaded && docLoaded) {
    return (
      <div className="appointment-list">
        <h3>Here are your upcoming appointments nigggaaaaa</h3>
        {appointmentList.map((appointment) => (
          <div className="doctor-container">
            <div className="left-container">
              <img className="profile-pic" src="profile.png"></img>
              <div className="doc-details">
                <p>{getDoctorName(appointment.doctor_id)}</p>
                <h2>Time</h2>
                <p>
                  {appointment.time_from} - {appointment.time_to}
                </p>
                <p></p>
              </div>
            </div>

            <div className="appointment-link"></div>
          </div>
        ))}
      </div>
    );
  } else {
    return <></>;
  }
};

export default AppointmentContainer;
