import React, { useState, useEffect, useRef } from "react";
import "./AppointmentContainer.css";
import axios, { post } from "axios";

const AppointmentContainer = (props) => {
  const [appointmentList, setAppointmentList] = useState(null);
  const [doctorList, setDoctorList] = useState(null);
  const [ApptLoaded, setApptLoaded] = useState(false);
  const [docLoaded, setDocLoaded] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);

  const fileInput = useRef(null);

  const patient_id = props.patient_id;

  useEffect(() => {
    async function getAppointments() {
      let appointmentQuery = await fetch(
        `/patient/get_appointments/${patient_id}`
      );
      let appointments = await appointmentQuery.json();
      appointments.sort(
        (a, b) => new Date(a.diary_date) - new Date(b.diary_date)
      );
      setAppointmentList(appointments);
      setApptLoaded(true);
    }

    getAppointments();
  }, []);

  useEffect(() => {
    async function getDoctors() {
      try {
        let docResponse = await fetch("/get_doctors");
        let doctors = await docResponse.json();
        setDoctorList(doctors);
        setDocLoaded(true);
      } catch (err) {
        console.log(err);
      }
    }
    getDoctors();
  }, []);

  const getDoctorName = (doctor_id) => {
    for (var i = 0; i < doctorList.length; i++) {
      if (doctorList[i].doctor_id === doctor_id) {
        return (
          <h3>
            Dr. {doctorList[i].first_name} {doctorList[i].last_name}
          </h3>
        );
      }
    }
  };

  const getDate = (appointment) => {
    const dateArray = appointment.diary_date.split(" ");
    const date = dateArray.slice(1, 4);
    var day = dateArray[0];
    return <div>{date.join("/")}</div>;
  };

  const getZoomUrl = (appointment) => {
    const zoomUrl = appointment.join_url;
    return (
      <a href={zoomUrl} target="_blank">
        Click here to begin your appointment
      </a>
    );
  };

  const deleteAppointment = (appointment_id) => {
    fetch("/delete_booking", {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },

      //make sure to serialize your JSON body
      body: JSON.stringify({
        patient_id: 1,
        diary_id: appointment_id,
      }),
    }).then((response) => {
      window.location.reload();
    });
  };

  function handeFileClick() {
    fileInput.current.click();
  }

  const fileHandler = (e) => {
    let files = e.target.files[0];

    for (var i = 0; i < files.length; i++) {
      files[i].document_type_id = 3;
      files[i].description = "";
    }

    setSelectedFiles([...selectedFiles, ...files]);
  };

  const uploadDocument = (event) => {
    event.preventDefault();
    const formData = new FormData();
    const url = `/upload/${patient_id}`;
    formData.append("file", selectedFiles);
    const config = {
      headers: {
        "content-type": "multipart/form-data",
      },
    };

    return post(url, formData, config);
    // const data = new FormData(selectedFiles);
    // console.log(data);
    // fetch(`/upload/${patient_id}`, {
    //   method: "POST",
    //   headers: {
    //     'content-type': 'multipart/form-data'
    //   },
    //   body: JSON.stringify(selectedFiles),
    // });
    // const formData = new FormData();
    // selectedFiles.forEach((file) => {
    //   console.log(file);
    //   fetch(`/upload/${patient_id}`, {
    //     method: "POST",
    //     headers: {
    //       Accept: "application/json",
    //       "Content-Type": "application/json",
    //     },
    //     body: JSON.stringify({
    //       file: file,
    //     }),
    //   }).then((response) => {
    //     console.log(response);
    //     // if (response.status === 201) {
    //     //   setSubmitted(true);
    //   });
    // });
  };

  const deleteDocument = (doc_id) => {
    fetch(`/delete_document`, {
      method: "DELETE",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        document_id: doc_id,
      }),
    }).then((response) => {
      // if (response.status === 201) {
      //   setSubmitted(true);
    });
  };

  if (ApptLoaded && docLoaded) {
    return (
      <div className="appointment-list">
        <div className="patient-appointment-intro">
          <h2>Please read carefully</h2>
          <p>
            If you wish to upload any medical information or images such as
            X-rays or CAT scans, please do so by clicking the 'Upload Files'
            button and attaching the files you wish to provide the Practitioner
          </p>
          <p>
            When it is time for your appointment, please click on the link
            underneath 'Zoom URL' to begin your video appointment.
          </p>
        </div>
        {appointmentList.map((appointment) => (
          <div className="appointment-container">
            <div className="calendar-container">
              <img className="calendar-pic" src="calendar.png"></img>
              <div className="appointment-box">
                <h2>{getDate(appointment)}</h2>
                <h3>
                  {appointment.time_from} - {appointment.time_to}
                </h3>
              </div>
            </div>
            <div className="appointment-box">
              <h2>Practitioner</h2>
              {getDoctorName(appointment.doctor_id)}
            </div>
            <div className="appointment-box">
              <h2>Upload Files</h2>
              <form
                // action={"/upload/" + patient_id}
                // method="POST"
                // encType="multipart/form-data"
                // target="hiddenFrame"
                onSubmit={uploadDocument}
              >
                <input
                  // style={{ display: "none" }}
                  name="file"
                  type="file"
                  onChange={(e) => fileHandler(e)}
                ></input>
                {/* <button>Send data!</button> */}
                <input type="submit" value="Submit" />
              </form>
              {/* <button onClick={handeFileClick}>Choose Files</button> */}
              {/* <button onClick={uploadDocument}>Submit</button> */}
            </div>
            <div className="appointment-box">
              <h2>Zoom URL</h2>
              {getZoomUrl(appointment)}
            </div>
            <div className="appointment-box">
              <h2>Cancel appointment</h2>
              <button
                onClick={() => {
                  deleteAppointment(appointment.diary_id);
                }}
              >
                Cancel your appointment
              </button>
            </div>
          </div>
        ))}
        <iframe
          name="hiddenFrame"
          width="0"
          height="0"
          border="0"
          style={{ display: "none" }}
        ></iframe>
      </div>
    );
  } else {
    return <></>;
  }
};

export default AppointmentContainer;
